import { createStore } from 'vuex'

export default createStore({
  state: {
    cartList: {
      // // 第一层
      // shopId: {
      //   shopName,
      //   productList: {
      //     productId: {

      //     }
      //   }
      // }
    }
  },
  getters: {
  },
  mutations: {
    // 两个参数，一个是store中的数据state，
    // 另一个是传过来的数据，用payload表示
    changeCartItem(state, payload) {
      // 拿到state，payload之后，
      // 就需要结合之前的数据结构往里面添加内容
      const { shopId, productId, productInfo, num } = payload
      let shopInfo = state.cartList[shopId]
      if(!shopInfo) {
        shopInfo = {}
      }
      let product = shopInfo.productList[productId]
      if(!product) {
        product = productInfo;
        product.count = 0
      }
      product.count = product.count + num
      // 只要往购物车中加东西了，商品选中状态就为true
      if(payload.num > 0) { product.check = true}
      if(product.count < 0) { product.count = 0}
      shopInfo.productList[productId] = product
      state.cartList[shopId] = shopInfo
    },
    changeShopName (state, payload) {
      const { shopId, shopName } = payload
      const shopInfo = state.cartList[shopId]
      // productList = shopInfo.productList
      if(!shopInfo) {
        shopName = ''
        productList: {}
      }
      shopInfo.shopName = shopName
    },
    changeCartItemChecked(state, payload) {
      const { shopId, productId } = payload
      let product = state.cartList[shopId][productId]
      product.check = !product.check
    },
    clearCartProducts(state, payload) {
      const { shopId } = payload
      state.cartList[shopId] = {}
    },
    allCheckProducts(state,payload) {
      const { shopId } = payload
      const productList = state.cartList[shopId]
      for(let i in productList) {
        let product = productList[i]
        if(!product.check){
          product.check = true
        }
      }
    }
  },
  actions: {
  },
  modules: {
  }
})
